import React from 'react';
import Square from './Square';

const Board = props => {
  const { squares, xIsNext } = props;
  return (
    <div className="board-row">
      {squares.map((value, square) => {
        return (
          <Square
            key={square.id}
            id={square}
            className="square"
            xIsNext={xIsNext}
            value={squares[square]}
            onClick={() => props.playerMove(square)}
          />
        );
      })}
    </div>
  );
};

