import React from 'react';
/*
function Square(props) {
  const winningSquareStyle = {
    backgroundColor: 'yellow'
  };
  const { winningSquare, onClick, value } = props;
  return (
    <button
      type="button"
      className="square"
      style={winningSquare ? winningSquareStyle : null}
      onClick={onClick}
    >
      {value}
    </button>
  );
} */

const Square = props => {
  const winningSquareStyle = {
    backgroundColor: 'yellow'
  };
  const whenToClick = cell => {
    return props.available ? props.onClick(cell) : null;
  };
  const { value, winningSquare } = props;
  return (
    <button
      type="button"
      className="square"
      style={winningSquare ? winningSquareStyle : null}
      onClick={() => whenToClick(props.id)}
    >
      {value}
    </button>
  );
};

export default Square;
