import React from 'react';

function Square(props) {
    const winningSquareStyle = {
        backgroundColor: 'yellow'
    };
    return (
        <button className="square " style={props.winningSquare ? winningSquareStyle : null}
            onClick={props.onClick}>
            {props.value}
        </button>
    );
}

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(400).fill(null),
            xIsNext: true,
        };
    }

    renderSquare(i) {
        let winningSquare = this.props.winner && this.props.winner.includes(i) ? true : false;
        return (
            <Square
                key={"square " + i}
                value={this.props.squares[i]}
                onClick={() => this.props.onClick(i)}
                winningSquare = {winningSquare}
            />
        );
    }

    renderSquares(n) {
        let squares = [];
        for (let i = n; i < n + 20; i++) {
            squares.push(this.renderSquare(i));
        }
        return squares;
    }

    renderRows(i) {
        return (
            <div className="board-row">{this.renderSquares(i)}</div>
        );
    }

    render() {
        return (
            <div>
                {this.renderRows(0)}
                {this.renderRows(20)}
                {this.renderRows(40)}
                {this.renderRows(60)}
                {this.renderRows(80)}
                {this.renderRows(100)}
                {this.renderRows(120)}
                {this.renderRows(140)}
                {this.renderRows(160)}
                {this.renderRows(180)}
                {this.renderRows(200)}
                {this.renderRows(220)}
                {this.renderRows(240)}
                {this.renderRows(260)}
                {this.renderRows(280)}
                {this.renderRows(300)}
                {this.renderRows(320)}
                {this.renderRows(340)}
                {this.renderRows(360)}
                {this.renderRows(380)}
            </div>
        );
    }
}

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            history: [{
                squares: Array(400).fill(null)
            }],
            stepNumber: 0,
            xIsNext: true,
            isDescending: true
        }
    }

    handleClick(i) {
        const history = this.state.history.slice(0, this.state.stepNumber);
        const current = history[history.length - 1];
        const squares = current.squares.slice();

        if (calculateWinwer(squares) || squares[i]) {
            return;
        }
        squares[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
            history: history.concat([{
                squares: squares
            }]),
            stepNumber: history.length,
            xIsNext: !this.state.xIsNext
        });
    }

    jumpTo(step) {
        this.setState({
            stepNumber: step,
            xIsNext: (step % 2) === 0
        });
    }

    sortHistory() {
        this.setState({
            isDescending: !this.state.isDescending
        });
    }

    render() {

        const history = this.state.history;
        const current = history[this.state.stepNumber];
        const winner = calculateWinwer(current.squares);

        const moves = history.map((step, move) => {
            const desc = move ?
                'Go to move #' + move :
                'Go to game start';
            return (
                <li key={move}>
                    <button onClick={() => this.jumpTo(move)}>
                        {move === this.state.stepNumber ? <b>{desc}</b> : desc}
                    </button>
                </li>
            );
        });

        let status;
        if (winner) {
            status = 'Winner:' + winner.player;
        } else {
            status = 'Next Player: ' + (this.state.xIsNext) ? 'X' : 'O';
        }
        return (
            <div className="game">
                <div className="game-board">
                    <Board
                        winner={winner && winner.line}
                        squares={current.squares}
                        onClick={i => this.handleClick(i)}
                    />
                </div>
                <div className="game-info">
                    <div>{status}</div>
                    <ol>{this.state.isDescending ? moves : moves.reverse()}</ol>
                    <button onClick={() => this.sortHistory()}>
                        Sort By: {this.state.isDescending ? "Descending" : "Ascending"}
                    </button>
                </div>
            </div>
        );
    }
}

function calculateWinwer(squares) {
    for (let i = 0; i < 400; i++) {
        if (squares[i] && squares[i] === squares[i - 20] && squares[i] === squares[i - 40] && squares[i] === squares[i + 20] && squares[i] === squares[i + 40] && squares[i + 60] !== squares[i - 60]) {
