/* eslint-disable react/no-unused-state */
import React from 'react';
import Square from './Square';

const Board = props => {
  const checkAvailability = () => {
    const { versus, playerStarts, xIsNext } = props;
    if (props.gameEnd) {
      return false;
    }
    if (versus === 'A') {
      return (playerStarts && xIsNext) || (!playerStarts && !xIsNext);
    }
    if (versus === 'P') {
      return true;
    }
    return false;
  };
  const { squares, xIsNext, playerMove } = props;
  return (
    <div className="board-row">
      {squares.map((value, square) => {
        return (
          <Square
            key={square.id}
            id={square}
            className="square"
            available={checkAvailability()}
            xIsNext={xIsNext}
            value={squares[square]}
            onClick={() => playerMove(square)}
          />
        );
      })}
    </div>
  );
};
export default Board;
