/* eslint-disable no-shadow */
import React from 'react';
import { connect } from 'react-redux';
import { Form, Icon, Input, Button, Checkbox, message } from 'antd';
import { loginAsync } from '../../modules/Login/actions/index';
import './login.css';

class LoginForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  handleSubmit = e => {
    const { form, loginAsync } = this.props;
    e.preventDefault();
    form.validateFields((err, values) => {
      if (!err) {
        message.loading('You Are You Logging IN....', 2.5).then(() => {
          if (loginAsync(values.email, values.password)) {
            message.success('login success', 1.0);
            window.location.href = '/';
          } else {
            message.success('login fail', 1.0);
            window.location.href = '/#login';
          }
        });
      }
    });
  };

  render() {
    const { form, loading } = this.props;
    const { getFieldDecorator } = form;
    return (
      <article className="mw5 center bg-white br3 pa3 pa4-ns mv3 ba b--black-10">
        <Form onSubmit={this.handleSubmit} className="login-form">
          <Form.Item>
            {getFieldDecorator('email', {
              rules: [
                { required: true, message: 'Please input your username!' }
              ]
            })(
              <Input
                prefix={
                  <Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />
                }
                placeholder="Email"
              />
            )}
          </Form.Item>
          <Form.Item>
            {getFieldDecorator('password', {
              rules: [
                { required: true, message: 'Please input your Password!' }
              ]
            })(
              <Input
                prefix={
                  <Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />
                }
                type="password"
                placeholder="Password"
              />
            )}
          </Form.Item>
          <Form.Item>
            {getFieldDecorator('remember', {
              valuePropName: 'checked',
              initialValue: true
            })(<Checkbox>Remember me</Checkbox>)}
            <a className="login-form-forgot" href="boo">
              Forgot password
            </a>
            <Button
              loading={loading}
              type="primary"
              htmlType="submit"
              className="login-form-button"
            >
              Log in
            </Button>
            Or <a href="/#register">register now!</a>
          </Form.Item>
        </Form>
      </article>
    );
  }
}

const form = Form.create()(LoginForm);

const mapStateToProps = state => ({
  user: state.authReducerLogin.user,
  error: state.authReducerLogin.error
});

const matchDispatchToProps = dispatch => ({
  loginAsync: (email, password) => dispatch(loginAsync(email, password))
});

export default connect(
  mapStateToProps,
  matchDispatchToProps
)(form);
