/* eslint-disable no-unreachable */
export const initialState = {
  grid: 400,
  streaks: [],
  history: [
    {
      squares: new Array(400).fill(null)
    }
  ],
  currentBoard: new Array(400).fill(null),
  stepNumber: 0,
  xIsNext: true,
  isDescending: true,
  endOfGame: false
};

export const gameReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'NEW_GAME':
      return {
        ...state,
        history: [
          {
            squares: new Array(state.grid).fill(null)
          }
        ],
        currentBoard: new Array(state.grid).fill(null),
        stepNumber: 0,
        xIsNext: true,
        isDescending: true
      };
      break;
    case 'END_GAME':
      return { ...state };
      break;
    case 'PLAYER_MOVE':
      return {
        ...state,
        history: action.history.concat([
          {
            squares: action.squares
          }
        ]),
        currentBoard: action.squares,
        stepNumber: action.history.length,
        xIsNext: !state.xIsNext
      };
      break;
    case 'TOGGLE_HISTORY':
      return { ...state };
      break;
    case 'TIME_TRAVEL':
      return {
        ...state,
        stepNumber: action.move,
        xIsNext: !(action.move % 2)
      };
      break;
    case 'SORT_HISTORY':
      return {
        ...state,
        isDescending: !state.isDescending
      };
      break;
    case 'SET_GRID_400':
      return {
        ...state,
        grid: 400,
        streaks: []
      };
      break;
    case 'SET_GRID_9':
      return {
        ...state,
        grid: 9,
        streaks: [
          [0, 1, 2],
          [3, 4, 5],
          [6, 7, 8],
          [0, 3, 6],
          [1, 4, 7],
          [2, 5, 8],
          [0, 4, 8],
          [2, 4, 6]
        ]
      };
      break;
    default:
      return state;
  }
};

export default gameReducer;
