function Square(props) {
  const winningSquareStyle = {
    backgroundColor: 'yellow'
  };
  const { winningSquare, value, onClick } = props;

  return (
    <button
      type="button"
      className="square"
      style={winningSquare ? winningSquareStyle : null}
      onClick={() => onClick}
    >
      {value}
    </button>
  );
}

export default Square;
