/* eslint-disable no-shadow */
import React from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import { Form, Input, Checkbox, Button, message } from 'antd';
import { registerAsync } from '../../modules/Register/actions/index';
import './register.css';

class RegistrationForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      confirmDirty: false,
      email: '',
      name: '',
      password: ''
    };
  }

  handleSubmit = e => {
    /*
    const { form, registerAsync } = this.props;
    e.preventDefault();
    form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        message.loading('You Are You Registering....', 2.5).then(() => {
          message.success('You Are Logged In!', 1.0);
          window.location.href = '/';
        });
        registerAsync(values.name, values.email, values.password);
      }
    });
    */
    e.preventDefault();

    if (this.validateForm()) {
      const apiBaseUrl = 'http://localhost:1288/api/';
      const { name, email, password } = this.state;
      const data = {
        name,
        user_email: email,
        password
      };

      const headers = {
        'Content-Type': 'application/json'
      };
      axios
        .post(`${apiBaseUrl}createUsers`, data, { headers })
        .then(response => {
          if (response.data.success) {
            localStorage.setItem(
              'u_code',
              encodeURIComponent(JSON.stringify(response.data.data))
            );

            localStorage.setItem('is_done', true);

            window.location.href = '/';

            console.log('Login successfull');
          } else {
            console.log(response.data.message);
          }
        })
        .catch(function(error) {
          console.log(error);
        });
    }
  };

  handleConfirmBlur = e => {
    const { confirmDirty } = this.state;
    const { value } = e.target;
    this.setState({ confirmDirty: confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue('password')) {
      callback('Two passwords that you enter is inconsistent!');
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { confirmDirty } = this.state;
    const { form } = this.props;
    if (value && confirmDirty) {
      form.validateFields(['confirm'], { force: true });
    }
    callback();
  };

  render() {
    const { form } = this.props;
    const { getFieldDecorator } = form;

    return (
      <article className="mw5 center bg-white br3 pa3 pa4-ns mv3 ba b--black-10">
        <Form onSubmit={this.handleSubmit}>
          <Form.Item label="Name">
            {getFieldDecorator('name', {
              rules: [
                {
                  type: 'name',
                  message: 'The input is not valid Name!'
                },
                {
                  required: true,
                  message: 'Please input your Name!'
                }
              ]
            })(<Input />)}
          </Form.Item>
          <Form.Item label="E-mail">
            {getFieldDecorator('email', {
              rules: [
                {
                  type: 'email',
                  message: 'The input is not valid E-mail!'
                },
                {
                  required: true,
                  message: 'Please input your E-mail!'
                }
              ]
            })(<Input />)}
          </Form.Item>
          <Form.Item label="Password" hasFeedback>
            {getFieldDecorator('password', {
              rules: [
                {
                  required: true,
                  message: 'Please input your password!'
                },
                {
                  validator: this.validateToNextPassword
                }
              ]
            })(<Input.Password />)}
          </Form.Item>

          <Form.Item>
            {getFieldDecorator('agreement', {
              valuePropName: 'checked'
            })(
              <Checkbox>
                I have read the <a href="boo">agreement</a>
              </Checkbox>
            )}
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              Register
            </Button>
          </Form.Item>
        </Form>
      </article>
    );
  }
}

const form = Form.create()(RegistrationForm);

const mapStateToProps = state => ({
  registering: state.registering
});

const matchDispatchToProps = dispatch => ({
  registerAsync: (name, email, password) =>
    dispatch(registerAsync(name, email, password))
});

export default connect(
  mapStateToProps,
  matchDispatchToProps
)(form);
