/* eslint-disable no-nested-ternary */
import React from 'react';

const Square = props => {
  const winningSquareStyle = {
    backgroundColor: 'yellow'
  };
  const { winningSquare, available, xIsNext, value, onClick } = props;
  const placeholder = available
    ? value === null
      ? xIsNext
        ? 'X'
        : 'O'
      : null
    : null;

  return (
    <button
      type="button"
      className="square"
      style={winningSquare ? winningSquareStyle : null}
      onClick={onClick}
    >
      {value} {placeholder}
    </button>
  );
};

export default Square;
