/* eslint-disable react/no-unused-state */
import React from 'react';
import Square from './Square';

const Board = props => {
  const checkWinningCell = cell => {
    const { winningRow } = props;
    let winningRows = [];
    if (winningRow) {
      winningRows =
        winningRow.filter(winningCell => winningCell === cell).length > 0
          ? 'win'
          : '';
    }
    return winningRow;
  };
  const checkAvailability = () => {
    const { versus, playerStarts, xIsNext } = props;
    if (props.gameEnd) {
      return false;
    }
    if (versus === 'A') {
      return (playerStarts && xIsNext) || (!playerStarts && !xIsNext);
    }
    if (versus === 'P') {
      return true;
    }
    return false;
  };
  const { squares } = props;
  return (
    <div className="board-row">
      {squares.map((value, square) => {
        return (
          <Square
            key={square.id}
            id={square}
            className={checkWinningCell(square)}
            available={checkAvailability()}
            xIsNext={props.xIsNext}
            value={props.squares[square]}
            onClick={() => props.playerMove(square)}
          />
        );
      })}
    </div>
  );
};
export default Board;
