import React from 'react';
import Square from './Square';
/*
const Board = props => {
  const { squares, xIsNext } = props;
  return (
    <div className="board-row">
      {squares.map((value, square) => {
        return (
          <Square
            key={square.id}
            id={square}
            className="square"
            xIsNext={xIsNext}
            value={squares[square]}
            onClick={() => props.playerMove(square)}
          />
        );
      })}
    </div>
  );
};
*/

class Board extends React.Component {
  // Create the 3 x 3 board
  createBoard(row, col) {
    const board = [];
    let cellCounter = 0;

    for (let i = 0; i < row; i += 1) {
      const columns = [];
      for (let j = 0; j < col; j += 1) {
        columns.push(this.renderSquare((cellCounter += 1)));
      }
      board.push(
        <div key={i} className="board-row">
          {columns}
        </div>
      );
    }

    return board;
  }

  renderSquare(i) {
    const { squares, onClick } = this.props;
    return (
      <Square key={i} value={squares[i]} onClick={() => props.playerMove(i)} />
    );
  }

  render() {
    return <div>{this.createBoard(20, 20)}</div>;
  }
}

export default Board;
