/* eslint-disable react/no-unused-state */
import React from 'react';
import Square from './Square';

const Board = props => {
  const renderSquare = i => {
    const { winner, squares } = this.props;
    const winningSquare = !!(winner && winner.includes(i));
    return (
      <Square
        key={`square ${i}`}
        value={squares[i]}
        onClick={() => props.playerMove(i)}
        winningSquare={winningSquare}
      />
    );
  };

  const renderSquares = n => {
    const squares = [];
    for (let i = n; i < n + 20; i += 1) {
      squares.push(this.renderSquare(i));
    }
    return squares;
  };

  const renderRows = i => {
    return <div className="board-row">{this.renderSquares(i)}</div>;
  };

  return (
    <div>
      {this.renderRows(0)}
      {this.renderRows(20)}
      {this.renderRows(40)}
      {this.renderRows(60)}
      {this.renderRows(80)}
      {this.renderRows(100)}
      {this.renderRows(120)}
      {this.renderRows(140)}
      {this.renderRows(160)}
      {this.renderRows(180)}
      {this.renderRows(200)}
      {this.renderRows(220)}
      {this.renderRows(240)}
      {this.renderRows(260)}
      {this.renderRows(280)}
      {this.renderRows(300)}
      {this.renderRows(320)}
      {this.renderRows(340)}
      {this.renderRows(360)}
      {this.renderRows(380)}
    </div>
  );
};

export default Board;
