/* eslint-disable no-shadow */
/* eslint-disable react/prefer-stateless-function */
import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { Link, withRouter } from 'react-router-dom';
import { loginAsync } from '../modules/Login/reducers/index';

import { newGame } from '../acctions/gameAction';

// import {login_Success} from '../modules/Login/reducers/index';

class MainMenu extends Component {
  newGame() {
    const { newGame } = this.props;
    newGame();
  }

  componentDidMount() {}

  render() {
    const { grid, newGame, user } = this.props;
    return (
      <div className="main_menu">
        <h2>Welcome to {user.name} Tic Tac Toe!</h2>
        <Link to="/game">
          <button
            type="button"
            onClick={() => newGame(grid)}
            className="menu_button"
          >
            Play Offline
          </button>
        </Link>
        <br />
        <Link to="/register">
          <button type="button" className="menu_button">
            Register
          </button>
        </Link>
        <Link to="/login">
          <button type="button" className="menu_button">
            Login
          </button>
        </Link>
        <Link to="/settings">
          <button type="button" className="menu_button">
            Settings Offline
          </button>
        </Link>
        <Link to="/online">
          <button type="button" className="menu_button">
            Play Online
          </button>
        </Link>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  grid: state.game.grid,
  user: state.login.user
});

const matchDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      newGame
    },
    dispatch
  );
};

export default withRouter(
  connect(
    mapStateToProps,
    matchDispatchToProps
  )(MainMenu)
);
