import React from 'react';
import Board from '../components/Board';

import { playerMove } from '../acctions/gameAction';

import '../style/App.css';

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.playerMove = this.playerMove.bind(this);
    this.calculateWinwer = this.calculateWinwer.bind(this);
  }

  playerMove(move) {
    const { history, stepNumber, xIsNext } = this.props;
    const historys = history.slice(0, stepNumber + 1);
    const current = historys[historys.length - 1];
    const squares = current.currentBoard.slice();

    if (this.calculateWinwer(squares) || squares[move]) {
      return;
    }
    squares[move] = xIsNext ? 'X' : 'O';
    playerMove(history, squares);
  }

  calculateWinwer(squares) {
    for (let i = 0; i < 400; i += 1) {}
    return { player: null, line: [null, null, null, null, null] };
  }

  handleClick(i) {
    const { history, stepNumber, xIsNext } = this.state;
    const historys = history.slice(0, stepNumber + 1);
    const current = historys[historys.length - 1];
    const squares = current.currentBoard.slice();

    if (this.calculateWinwer(squares) || squares[i]) {
      return;
    }
    squares[i] = xIsNext ? 'X' : 'O';
    this.setState({
      history: historys.concat([
        {
          squares
        }
      ]),
      stepNumber: historys.length,
      xIsNext: !xIsNext
    });
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: step % 2 === 0
    });
  }

  sortHistory() {
    const { isDescending } = this.state;
    this.setState({
      isDescending: !isDescending
    });
  }

  render() {
    const {
      currentBoard,
      history,
      stepNumber,
      xIsNext,
      isDescending
    } = this.props;

    // const current = history[stepNumber];
    const winner = this.calculateWinwer(currentBoard);

    const moves = history.map((step, move) => {
      const desc = move ? `Go to move #${move}` : 'Go to game start';
      return (
        <li key={move.id}>
          <button type="button" onClick={() => this.jumpTo(move)}>
            {move === stepNumber ? <b>{desc}</b> : desc}
          </button>
        </li>
      );
    });

    let status;
    if (winner) {
      status = `Winner:${winner.player}`;
    } else {
      status = `Next player: ${xIsNext ? 'X' : 'O'}`;
    }
    return (
      <div className="game">
        <div className="game-board">
          <Board
            winner={winner && winner.line}
            squares={currentBoard}
            playerMove={i => this.playerMove(i)}
          />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <ol>{isDescending ? moves : moves.reverse()}</ol>
          <button type="button" onClick={() => this.sortHistory()}>
            Sort By: {isDescending ? 'Descending' : 'Ascending'}
          </button>
        </div>
      </div>
    );
  }
}

export default Game;
