import React from 'react';

function Square(props) {
    return (
        <button className="square" onClick={() => props.onClick()}>
            {props.value}
        </button>
    );
}

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(400).fill(null),
            xIsNext: true,
        };
    }

    renderSquare(i) {
        return (
            <Square
                key={"squases" + i}
                value={this.props.squares[i]}
                onClick={() => this.props.onClick(i)}
            />
        );
    }

    renderSquares(n){
        let squares = [];
        for( let i = n;i< n + 20; i++){
            squares.push(this.renderSquare(i));
        }
        return squares;
    }

    renderRows(i){
        return <div className="board-row">{this.renderSquares(i)}</div>;
    }

    render() {
        const winner = calculateWinwer(this.state.squares);
        let status;
        if (winner) {
            status = 'Winner: ' + winner;
        }
        else {
            status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
        }

        return (
            <div>
                <div className="status">{status}</div>
                    {this.renderRows(0)}
                    {this.renderRows(20)}
                    {this.renderRows(40)}
                    {this.renderRows(60)}
                    {this.renderRows(80)}
                    {this.renderRows(100)}
                    {this.renderRows(120)}
                    {this.renderRows(140)}
                    {this.renderRows(160)}
                    {this.renderRows(180)}
                    {this.renderRows(200)}
                    {this.renderRows(220)}
                    {this.renderRows(240)}
                    {this.renderRows(260)}
                    {this.renderRows(280)}
                    {this.renderRows(300)}
                    {this.renderRows(320)}
                    {this.renderRows(340)}
                    {this.renderRows(360)}
                    {this.renderRows(380)}

            </div>
        );
    }
}

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            history: [{
                squares: Array(400).fill(null)
            }],
            stepNumber: 0,
            xIsNext: true
        }
    }

    handleClick(i) {
        const history = this.state.history.slice(0, this.state.stepNumber + 1);
        const current = history[history.length - 1];
        const squares = current.squares.slice();

        if (calculateWinwer(squares) || squares[i]) {
            return;
        }
        squares[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
            history: history.concat([{
                squares: squares
            }]),
            stepNumber: history.length,
            xIsNext: !this.state.xIsNext
        });
    }

    jumpTo(step) {
        this.setState({
            stepNumber: step,
            xIsNext: (step % 2) === 0
        });
    }

    render() {
        const history = this.state.history;
        const current = history[this.state.stepNumber];
        const winner = calculateWinwer(current.squares);

        const moves = history.map((step, move) => {
            const desc = move ?
                'Go to move #' + move :
                'Go to game start';
            return (
                <li key={move}>
                    <button onClick={() => this.jumpTo(move)}>
                    {move == this.state.stepNumber ? <b>{desc}</b> : desc}
                    </button>
                </li>
            );
        });

        let status;
        if (winner) {
            status = 'Winner:' + winner;
        } else {
            status = 'Next Player: ' + (this.state.xIsNext) ? 'X' : 'O';
        }
        return (
            <div className="game">
                <div className="game-board">
                    <Board
                        squares={current.squares}
                        onClick={i => this.handleClick(i)}
                    />
                </div>
                <div className="game-info">
                    <div>{status}</div>
                    <ol>{moves}</ol>
                </div>
            </div>
        );
    }
}

function calculateWinwer(squares) {
    for (let i = 0; i < 400; i++) {
        if (squares[i] && squares[i] === squares[i - 20] && squares[i] === squares[i - 40] && squares[i] === squares[i + 20] && squares[i] === squares[i + 40] && ( squares[i + 60] !== squares[i - 60] )
            || squares[i] && squares[i] === squares[i - 20] && squares[i] === squares[i - 40] && squares[i] === squares[i - 60] && squares[i] === squares[i - 80] && ( squares[i + 20] !== squares[i - 100] )
            || squares[i] && squares[i] === squares[i + 20] && squares[i] === squares[i + 40] && squares[i] === squares[i + 60] && squares[i] === squares[i + 80] && ( squares[i + 100] !== squares[i - 20] )
            || squares[i] && squares[i] === squares[i - 1] && squares[i] === squares[i - 2] && squares[i] === squares[i + 1] && squares[i] === squares[i + 2] && ( squares[i + 3] !== squares[i - 3] )
            || squares[i] && squares[i] === squares[i - 1] && squares[i] === squares[i - 2] && squares[i] === squares[i - 3] && squares[i] === squares[i - 4] && ( squares[i - 5] !== squares[i + 1] )
            || squares[i] && squares[i] === squares[i + 1] && squares[i] === squares[i + 2] && squares[i] === squares[i + 3] && squares[i] === squares[i + 4] && ( squares[i + 5] !== squares[i - 1] )
            || squares[i] && squares[i] === squares[i - 19] && squares[i] === squares[i - 38] && squares[i] === squares[i + 19] && squares[i] === squares[i + 38] && ( squares[i + 57] !== squares[i - 57] )
            || squares[i] && squares[i] === squares[i - 19] && squares[i] === squares[i - 38] && squares[i] === squares[i - 57] && squares[i] === squares[i - 76] && ( squares[i + 19] !== squares[i - 95] )
            || squares[i] && squares[i] === squares[i + 19] && squares[i] === squares[i + 38] && squares[i] === squares[i + 57] && squares[i] === squares[i + 76] && ( squares[i + 95] !== squares[i - 19] )
            || squares[i] && squares[i] === squares[i - 21] && squares[i] === squares[i - 42] && squares[i] === squares[i + 21] && squares[i] === squares[i + 42] && ( squares[i + 63] !== squares[i - 63] )
            || squares[i] && squares[i] === squares[i + 21] && squares[i] === squares[i + 42] && squares[i] === squares[i + 63] && squares[i] === squares[i + 84] && ( squares[i + 105] !== squares[i - 23] )
