/* eslint-disable no-nested-ternary */
import React from 'react';

const Square = props => {
  const winningSquareStyle = {
    backgroundColor: 'yellow'
  };
  const { winningSquare, available, xIsNext, value, className } = props;
  // const name = value === null ? 'square' : `square_filled ${className}`;
  const placeholder = available
    ? value === null
      ? xIsNext
        ? 'X'
        : 'O'
      : null
    : null;
  const whenToClick = square => {
    return available ? props.onClick(square) : null;
  };
  return (
    <button
      type="button"
      className="square"
      style={winningSquare ? winningSquareStyle : null}
      onClick={() => whenToClick(props.id)}
    >
      {value} {placeholder}
    </button>
  );
};

export default Square;
